jQuery(document).ready(function() {
	jQuery("#aviso").on("click", function() {
        jQuery("#inputAviso").prop('checked',true);
    });
	
	jQuery("#avisoP").on("click", function() {
        jQuery("#inputAvisoP").prop('checked',true);
    });
});
	
function onSubmit(token) {
    var nombre = jQuery("input[name='inputNombre']").val();
    var apellidos = jQuery("input[name='inputApellidos']").val();
    var tel = jQuery("input[name='inputTelefono']").val();
    var email = jQuery("input[name='inputEmail']").val();
    if(!jQuery("input[name='inputAviso']").prop('checked')){
        alert('Tienes que leer el aviso legal');
        event.preventDefault();
    }else{
        if(nombre == ''){
            alert('El campo nombre esta vacío');
            event.preventDefault();
        }else if(apellidos == ''){
            alert('El campo apellidos esta vacío');
            event.preventDefault();
        }else if(tel == ''){
            alert('El campo teléfono esta vacío o no es válido');
            event.preventDefault();
		}else if(email == ''){
            alert('El campo email esta vacío o no es válido');
            event.preventDefault();
        }
    }
    document.getElementById("formCandidatos").submit();
}

